
public class ClasseMath {
	
	public static void main(String[] args) {
		//TODO Auto-generated method stub
		
		//compra e retorna o maior numero de dois numeros
		System.out.println("O maior número entre 9 e 2 é: " + Math.max(9, 2));
		
		//retorna o menos entre dois numeros
		System.out.println("O maior número entre 9 e 2 é: " + Math.min(1, 9));

		
		System.out.println("A raiz quadrada de 9 é : " + Math.sqrt(81));
		System.out.println("O numero aleatorio gerado é : " + Math.random());
		
		//Round - arredonda numeros
		System.out.println("O arrendondamento de 7.8 é : " + Math.round(7.8));
		System.out.println("O arrendondamento de 7.4 é: " + Math.round(7.4));

		
	}

}
